import React, { useState } from 'react';
import './Intro.css';
import img_0 from '../attachments/photos/graduated.jpg';
import img_1 from '../attachments/photos/family.jpg';


function Intro() {
    const [expandedImage, setExpandedImage] = useState(null);

    const handleImageClick = (image) => {
        if (expandedImage === image) {
        setExpandedImage(null);
        } else {
        setExpandedImage(image);
        }
    }; 

    return (
        <div id="intro-container" className="section">
            <h1>What's up?</h1>
            <div className='subsection'>
                <div className="context">

                    <h3>Let me tell you a little bit about me:</h3>

                    <h5>
                        I am 23 years olds, and live in the best city in the world - NYC! <br />
                        I graduated in 2022 and have wobbled around various jobs and research opportunities remotely and in Boston, which can be seen in detail
                    </h5>
                </div>
                <div className="gallery">
                    <div
                        className={`gallery-item ${expandedImage === img_0 ? 'expanded' : ''}`}
                        onClick={() => handleImageClick(img_0)}
                    >
                        <img src={img_0} alt="Graduated!" />
                        <div className="description">Graduated!</div>
                    </div>
                    <div
                        className={`gallery-item ${expandedImage === img_1 ? 'expanded' : ''}`}
                        onClick={() => handleImageClick(img_1)}
                    >
                        <img src={img_1} alt="My family!" />
                        <div className="description">My family!</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Intro;
